mapper.insert:
INSERT INTO attach (
	atch_no
	,atch_parent_no
	,atch_category
	,atch_file_name
	,atch_original_name
	,atch_file_size
	,atch_convert_size
	,atch_content_type
	,atch_path


	,atch_reg_id

) VALUES (
	seq_attach.nextval
	,#{atchParentNo}
	,#{atchCategory}
	,#{atchFileName}
	,#{atchOriginalName}
	,#{atchFileSize}
	,#{atchConvertSize}
	,#{atchContentType}
	,#{atchPath}


	,#{atchRegId}

)

mapper.select:
	select
         a.*
    from(    
        SELECT
            atch_no 
        FROM
            attach 
        WHERE
            atch_parent_no = #{memId}
			AND atch_category = #{atchCategory} 
        order by atch_no desc    
            )a        
     where rownum = 1
